// src/components/Watch.js
import React, { useEffect, useState, useRef } from 'react';
import { useLocation, useNavigate, useSearchParams } from 'react-router-dom';
import api from '../Api';
import { useAuth } from '../authContext';

export default function Watch() {
    const [searchParams] = useSearchParams();
    const path = searchParams.get('path');
    const movieFolder = searchParams.get('movie');
    const navigate = useNavigate();
    const { accessToken } = useAuth();
    const videoRef = useRef(null);
    const [videoFileName, setVideoFileName] = useState('');
    const [contentType, setContentType] = useState('movie');
    const { user } = useAuth();
    const [hasSubtitle, setHasSubtitle] = useState(false);
    const [showNextButton, setShowNextButton] = useState(false);
    const [metadata, setMetadata] = useState(null);
    const [nextEpisodePath, setNextEpisodePath] = useState(null);
    const containerRef = useRef(null);
    const location = useLocation();

    useEffect(() => {
        if (movieFolder) {
            const fetchMetadata = async () => {
                try {
                    const res = await api.get(`/api/movie-metadata/${encodeURIComponent(movieFolder)}`);
                    setMetadata(res.data);
                } catch (err) {
                    console.error('âŒ Failed to load movie metadata:', err);
                }
            };
            fetchMetadata();
        }
    }, [movieFolder]);

    useEffect(() => {
        if (path) {
            const filename = path.split('/').pop();
            setVideoFileName(filename);
            setContentType('series');
        } else if (movieFolder && metadata?.filename) {
            setVideoFileName(metadata.filename);
            setContentType('movie');
        }
    }, [path, movieFolder, metadata]);

    //Save progress for series and movies
    useEffect(() => {
        const saveProgress = () => {
            if (videoRef.current && user && videoFileName) {
                const time = videoRef.current.currentTime;
                const duration = videoRef.current.duration;

                api.post('/api/progress', {
                    fileName: videoFileName,
                    type: contentType,
                    time,
                    duration, // âœ… include duration
                    ...(contentType === 'series' && path ? { fullPath: path } : {})
                }).catch(err => console.error('âŒ Save progress error:', err.response?.data || err.message));
            }
        };

        const interval = setInterval(saveProgress, 2000); // auto-save every 2 seconds

        return () => {
            saveProgress(); // final save on unmount
            clearInterval(interval);
        };
    }, [videoFileName, contentType, user, path]);

    //Fetch progress
    useEffect(() => {
        const fetchProgress = async () => {
            if (videoFileName) {
                try {
                    const res = await api.get(`/api/progress?fileName=${videoFileName}`);
                    const savedTime = res.data?.time;
                    if (videoRef.current && savedTime) {
                        videoRef.current.currentTime = savedTime;
                    }
                } catch (err) {
                    console.error('âŒ Fetch progress error:', err.response?.data || err.message);
                }
            }
        };
        fetchProgress();
    }, [videoFileName]);

    //Keeps track of current video progress (used for rendering next episode button)
    useEffect(() => {
        const video = videoRef.current;
        if (!video) return;

        const handleTimeUpdate = () => {
            if (video.duration && video.currentTime / video.duration > 0.96) {
                setShowNextButton(true);
            }
        };

        video.addEventListener('timeupdate', handleTimeUpdate);

        return () => {
            video.removeEventListener('timeupdate', handleTimeUpdate);
        };
    }, []);

    //Find the next episode in order to play it when the current one is done
    useEffect(() => {
        const fetchNextEpisode = async () => {
            if (!path) return;

            const parts = path.split('/');
            const currentFilename = parts.at(-1);
            const seasonFolder = parts.at(-2);
            const seriesName = parts.slice(0, -2).join('/'); // âœ… FIXED HERE

            console.log('ðŸ“º Fetching next episode with:', {
                path,
                currentFilename,
                seasonFolder,
                seriesName
            });

            try {
                const res = await api.get(`/api/series/${encodeURIComponent(seriesName)}`);
                console.log('ðŸ“¦ API response:', res.data);

                if (!res.data?.seasons) {
                    console.warn('âš ï¸ Missing "seasons" in response');
                    return;
                }

                const seasonData = res.data.seasons.find(s => s.season === seasonFolder);
                if (!seasonData) {
                    console.warn(`âš ï¸ Season '${seasonFolder}' not found`);
                    return;
                }

                const sortedEpisodes = seasonData.episodes.sort((a, b) => {
                    const num = (title) => parseInt(title.match(/\d+/)?.[0] || '0', 10);
                    return num(a.title) - num(b.title);
                });

                const currentIndex = sortedEpisodes.findIndex(ep =>
                    ep.path.endsWith(currentFilename)
                );

                const nextEpisode = sortedEpisodes[currentIndex + 1];

                if (nextEpisode) {
                    console.log('âž¡ï¸ Next episode found:', nextEpisode.path);
                    setNextEpisodePath(nextEpisode.path);
                } else {
                    console.log('âœ… No next episode available');
                }
            } catch (err) {
                console.error('âŒ Failed to fetch next episode:', err);
            }
        };

        fetchNextEpisode();
    }, [path]);

    const videoUrl = (() => {
        if (path && accessToken) {
            return `${api.defaults.baseURL}/api/stream/file?path=${encodeURIComponent(path)}&token=${accessToken}`;
        }

        if (movieFolder && metadata?.filename && accessToken) {
            return `${api.defaults.baseURL}/api/stream/movie/${encodeURIComponent(movieFolder)}/${encodeURIComponent(metadata.filename)}?token=${accessToken}`;
        }

        return null;
    })();

    const subtitleUrl = (() => {
        if (path) {
            const vttPath = path.replace(/\.[^.]+$/, '.vtt');
            return `${api.defaults.baseURL}/media/Series/${encodeURIComponent(vttPath)}`;
        }

        if (movieFolder && metadata?.filename) {
            const vttPath = `${movieFolder}/${metadata.filename}`.replace(/\.[^.]+$/, '.vtt');
            return `${api.defaults.baseURL}/media/Movies/${encodeURIComponent(vttPath)}`;
        }

        return null;
    })();

    //Check for subtitles
    useEffect(() => {
        const checkSubtitle = async () => {
            if (!subtitleUrl) return;

            try {
                await fetch(subtitleUrl, { method: 'HEAD' });
                setHasSubtitle(true);
            } catch {
                setHasSubtitle(false);
            }
        };

        checkSubtitle();
    }, [subtitleUrl]);

    useEffect(() => {
        setShowNextButton(false);
    }, [path]);

    return (
        <div key={path} className="video-overlay">
            <div className="video-wrapper" ref={containerRef}>
                <button className="close-button" onClick={() => navigate(-1)}>âœ–</button>
                {showNextButton && nextEpisodePath !== path && (
                    <button
                        className="next-episode-button"
                        onClick={() => {
                            setShowNextButton(false); // ðŸ‘ˆ Reset before navigating
                            navigate(`/watch?path=${encodeURIComponent(nextEpisodePath)}`);
                        }}
                    >
                        â–¶ Watch Next Episode
                    </button>
                )}

                <div className="video-container">
                    {videoUrl ? (
                        <video
                            ref={videoRef}
                            className="video-player"
                            autoPlay
                            crossOrigin="anonymous"
                            controls
                            controlsList="nodownload noplaybackrate"
                            disablePictureInPicture
                        >
                            <source src={videoUrl} type="video/mp4" />
                            {hasSubtitle && (
                                <track
                                    label="English"
                                    kind="subtitles"
                                    srcLang="en"
                                    src={subtitleUrl}
                                    default
                                />
                            )}
                            Your browser does not support the video tag.
                        </video>
                    ) : (
                        <p>Loading video...</p>
                    )}
                </div>
            </div>
        </div>
    );
}
