import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';

const API_URL = process.env.REACT_APP_API_URL;

function SeriesRow({ title, topSeries = false }) {
  const [seriesList, setSeriesList] = useState([]);
  const navigate = useNavigate();

  const metadataCache = useRef({}); //in-memory cache

  useEffect(() => {
      const fetchSeries = async () => {
          const token = localStorage.getItem('token');
          try {
              const res = await fetch(`${API_URL}/api/series`, {
                  headers: { Authorization: `Bearer ${token}` },
              });
              const folders = await res.json();
  
              const seriesWithMetadata = await Promise.all(
                  folders.map(async (folder) => {
                      // Use cache if available
                      if (metadataCache.current[folder]) {
                          return {
                              folder,
                              ...metadataCache.current[folder],
                          };
                      }
  
                      // Otherwise fetch from API
                      const metaRes = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(folder)}`);
                      const metaData = await metaRes.json();
  
                      const metadata = {
                          poster: metaData.Poster && metaData.Poster !== 'N/A' ? metaData.Poster : null,
                          title: folder,
                          imdbRating: metaData.imdbRating || 'N/A',
                          genre: metaData.Genre || '',
                      };
  
                      metadataCache.current[folder] = metadata; // ✅ Save to cache
  
                      return {
                          folder,
                          ...metadata,
                      };
                  })
              );
  
              setSeriesList(seriesWithMetadata);
  
              const allGenres = seriesWithMetadata.flatMap(series =>
                  series.genre.split(',').map(g => g.trim())
              );
              setGenres([...new Set(allGenres)]);
          } catch (err) {
              console.error('Error fetching series folders or metadata:', err);
          }
      };
  
      fetchSeries();
  }, []);
  

  const handleSeriesClick = (folderName) => {
    navigate(`/series?name=${encodeURIComponent(folderName)}`);
  };

  return (
    <div className="movie-row">
      <h3>{title}</h3>
      <div className='centreraCards'>
        <div className="cards">
          {seriesList.map(series => (
            <div
              key={series.folder}
              className="movie-card"
              onClick={() => handleSeriesClick(series.folder)}
            >
              {series.poster && (
                <img src={series.poster} alt={series.title} className="movie-poster" />
              )}
              <span className="movieTitle">{series.title} ⭐{series.imdbRating}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SeriesRow;
