import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState, useRef } from 'react';

const API_URL = process.env.REACT_APP_API_URL;

function MovieRow({ title, setSelectedMovie, topPicks = false }) {
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('');

    useEffect(() => {
        const fetchMovies = async () => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`${API_URL}/api/movies`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const movieData = await res.json();

                setMovies(movieData);
            } catch (err) {
                console.error('Error fetching movies:', err);
            }
        };

        fetchMovies();
    }, [topPicks]);

    const genres = Array.from(new Set(movies.flatMap((m) => m.genre?.split(', ').filter(Boolean))));

    const filteredMovies = movies.filter((movie) => {
        const matchesSearch =
            movie.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            movie.genre?.toLowerCase().includes(searchTerm.toLowerCase());

        const matchesGenre = selectedGenre ? movie.genre?.includes(selectedGenre) : true;

        return matchesSearch && matchesGenre;
    });

    return (
        <div className="movie-row">
            <h3 className="msTitel">{title}</h3>
            <div className="searchBox">
                <div className="sokDiv">
                    <input
                        type="text"
                        placeholder="Search movie titles"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="msSearch"
                    />
                </div>
                <div className="centreraCards">
                    <div className="genre-buttons">
                        <button onClick={() => setSelectedGenre('')} className={!selectedGenre ? 'active' : ''}>
                            All
                        </button>
                        {genres.map((genre, index) => (
                            <button
                                key={index}
                                onClick={() => setSelectedGenre(genre)}
                                className={selectedGenre === genre ? 'active' : ''}
                            >
                                {genre}
                            </button>
                        ))}
                    </div>
                </div>
            </div>
            <div className="centreraCards">
                <div className="cards">
                    {filteredMovies.map((movie) => (
                        <div
                            className="movie-card"
                            key={`${title}-${movie.filename}`}
                            onClick={() => setSelectedMovie({ filename: movie.filename })}
                        >
                            <div className="hover-area">
                                {movie.poster && (
                                    <img
                                        src={`/moviePosters/${encodeURIComponent(movie.title)}.jpg`}
                                        alt={movie.title}
                                    />
                                )}
                                <span className="movieTitle">
                                    {movie.title} ‚≠ê{movie.imdbRating}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default MovieRow;
