import React, { useEffect, useState, useRef } from 'react';
import movieTitles from './moviesTitles.json';

const API_URL = process.env.REACT_APP_API_URL;

function MovieRow({ title, setSelectedMovie, topPicks = false }) {
    const [movies, setMovies] = useState([]);
    const metadataCache = useRef({});

    useEffect(() => {
        const fetchMovies = async () => {
            const token = localStorage.getItem('token');
            try {
                let filenames = [];

                if (topPicks) {
                    const res = await fetch(`${API_URL}/api/top-picks`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    const topTitles = await res.json(); // these are display titles from logs

                    // Reverse-lookup filenames from titles
                    filenames = topTitles.map((title) => {
                        const match = Object.entries(movieTitles).find(([filename, mappedTitle]) => mappedTitle === title);
                        return match ? match[0] : null;
                    }).filter(Boolean);
                } else {
                    const res = await fetch(`${API_URL}/api/movies`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    const allFilenames = await res.json();

                    filenames = allFilenames.filter((filename) => movieTitles[filename]);
                }

                const metadataPromises = filenames.map(async (filename) => {
                    const searchTitle = movieTitles[filename];

                    // Cache hit
                    if (metadataCache.current[searchTitle]) {
                        return {
                            filename,
                            ...metadataCache.current[searchTitle],
                        };
                    }

                    // Fetch metadata
                    const metaRes = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(searchTitle)}`);
                    const metaData = await metaRes.json();

                    const metadata = {
                        title: metaData.Title || searchTitle,
                        poster: metaData.Poster && metaData.Poster !== 'N/A' ? metaData.Poster : null,
                        imdbRating: metaData.imdbRating || 'N/A',
                        genre: metaData.Genre || '',
                    };

                    metadataCache.current[searchTitle] = metadata;

                    return {
                        filename,
                        ...metadata,
                    };
                });

                const movieWithMetadata = await Promise.all(metadataPromises);
                setMovies(movieWithMetadata);
            } catch (err) {
                console.error('Error fetching movies or metadata:', err);
            }
        };

        fetchMovies();
    }, [topPicks]);


    return (
        <div className='centreraCards'>
            <div className='homeComponent'>
                <h3>{title}</h3>
                <div className="cards" id='homeCards'>
                    {movies.map((movie) => (
                        <div
                            className="movie-card"
                            key={`${title}-${movie.filename}`}
                            onClick={() => setSelectedMovie({ filename: movie.filename })}
                        >
                            <div className="hover-area">
                                {movie.poster && (
                                   <img
                                   src={`/moviePosters/${encodeURIComponent(movie.title)}.jpg`}
                                   alt={movie.title}
                                 />                                 
                                )}
                                <span className='movieTitle'>{movie.title} ‚≠ê{movie.imdbRating}</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default MovieRow;
