// src/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../authContext';
import Header from '../Components/Header';
import api from '../Api';

function Login() {
  const { login } = useAuth();
  const { isLoggedIn } = useAuth();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault(); // prevents page reload

    try {
      const res = await fetch(`${API_URL}/api/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.status === 403) {
        setMessage('❌ Your account is not yet approved.');
      } else if (res.ok) {
        login(data.token);
        setMessage(`✅ ${data.message || 'Logged in!'}`);
        setTimeout(() => navigate('/'), 600);
      } else {
        setMessage(`❌ ${data.message || 'Login failed.'}`);
      }
    } catch (err) {
      console.error('Login error:', err);
      setMessage('❌ Something went wrong. Please try again.');
    }
  };

  return (
    <>
      <Header />
      <div className='mainDiv'>
        <div className="form-container">
          <h2>Sign in</h2>
          <form onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button className="authKnapp" type="submit">
              Sign In
            </button>
          </form>
          {message && (
            <p className={`auth-message ${message.startsWith('✅') ? 'success' : 'error'}`}>{message}</p>
          )}
          {!isLoggedIn && (
            <div className='authDiv'>
              <p className="auth-message">Don’t have an account?</p>
              <Link to="/register"><p className="auth-message2">Register here</p></Link>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default Login;
