import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const API_URL = process.env.REACT_APP_API_URL;

function RecentlyAddedRow({ title, setSelectedMovie }) {
    const [items, setItems] = useState([]);
    const navigate = useNavigate();
    const metadataCache = useRef({});

    useEffect(() => {
        const fetchRecent = async () => {
            const token = localStorage.getItem('token');
            try {
                    const match = Object.entries(moviesMetadata).find(([folderName, meta]) =>
                      meta.Title?.toLowerCase() === title.toLowerCase()
                    );
                  
                    if (!match) return null;
                  
                    const [actualFolderName, meta] = match;
                  
                    return {
                      type: 'movie',
                      fileName: meta.filename,
                      title: meta.Title,
                      folder: actualFolderName // ✅ Use actual folder name
                    };
                  
                const data = await res.json();

                const enriched = await Promise.all(data.map(async (item) => {
                    const cacheKey = item.title;

                    if (metadataCache.current[cacheKey]) {
                        return { ...item, ...metadataCache.current[cacheKey] };
                    }

                    let meta;
                    try {
                        if (item.type === 'movie') {
                            const res = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(item.title)}`, {
                                headers: { Authorization: `Bearer ${token}` }
                            });
                            meta = await res.json();
                        } else {
                            const res = await fetch(`${API_URL}/api/series-metadata/${encodeURIComponent(item.seriesName)}`, {
                                headers: { Authorization: `Bearer ${token}` }
                            });
                            meta = await res.json();
                        }
                    } catch (err) {
                        console.warn(`Failed to load metadata for: ${item.title}`);
                        return item;
                    }

                    const metadata = {
                        title: meta.Title || item.title,
                        imdbRating: meta.imdbRating || 'N/A',
                        genre: meta.Genre || '',
                    };

                    metadataCache.current[cacheKey] = metadata;

                    return { ...item, ...metadata };
                }));

                setItems(enriched);
            } catch (err) {
                console.error('Failed to load recent items:', err);
            }
        };

        fetchRecent();
    }, []);

    return (
        <div className='centreraCards'>
            <div className='homeComponent'>
                <h3>{title}</h3>
                <div className="cards" id='homeCards'>
                    {items.map(item => (
                        <div
                            key={`${item.type}-${item.title}`}
                            className="movie-card"
                            onClick={() => {
                                if (item.type === 'movie') {
                                    setSelectedMovie({
                                        filename: item.fileName,
                                        folder: item.folder
                                    });
                                    navigate(`?play=${encodeURIComponent(item.fileName)}&folder=${encodeURIComponent(item.folder)}`);
                                } else {
                                    navigate(`/series?name=${encodeURIComponent(item.seriesName)}`);
                                }
                            }}
                        >
                            <div className="hover-area">
                                <img
                                    src={`/${item.type === 'movie' ? 'moviePosters' : 'seriesPosters'}/${encodeURIComponent(item.folder || item.seriesName)}.jpg`}
                                    alt={item.title}
                                    onError={(e) => (e.target.style.display = 'none')}
                                />
                                <span className='movieTitle'>{item.title} ⭐{item.imdbRating}</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default RecentlyAddedRow;
