import { useAuth } from './authContext';
import React, { useEffect, useState, useRef } from 'react';
import Header from './Header';
import { useNavigate, useLocation } from 'react-router-dom';


const API_URL = process.env.REACT_APP_API_URL;

function Movies() {
    const { isLoggedIn } = useAuth();
    const navigate = useNavigate();
    const location = useLocation();

    const [selectedMovie, setSelectedMovie] = useState(null);

    // Sync selectedMovie with URL state
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const play = params.get('play');

        if (!play && selectedMovie) {
            setSelectedMovie(null);
        }

        if (play && !selectedMovie) {
            setSelectedMovie({ filename: play });
        }
    }, [location.search, selectedMovie]);

    return (
        <>
            <Header />
            <div className='mainDiv'>
                {isLoggedIn && (
                    <>
                        <MovieRow
                            title="Movies"
                            selectedMovie={selectedMovie}
                            setSelectedMovie={(movie) => {
                                setSelectedMovie(movie);
                                navigate(`?play=${encodeURIComponent(movie.filename)}`);
                            }}
                        />

                    </>
                )}

                {selectedMovie && (
                    <VideoOverlay
                        filename={selectedMovie.filename}
                        onClose={() => {
                            setSelectedMovie(null);
                            navigate('', { replace: true }); // clear ?play from URL
                        }}
                    />
                )}
            </div>
        </>
    );
}

function MovieRow({ title, setSelectedMovie }) {
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('');
    const metadataCache = useRef({});

    useEffect(() => {
        const fetchMovies = async () => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`${API_URL}/api/movies`, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const movieList = await res.json();

                const metadataPromises = movieList.map(async ({ filename, title }) => {
                    if (metadataCache.current[title]) {
                        return {
                            filename,
                            ...metadataCache.current[title],
                        };
                    }

                    const metaRes = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(filename)}`);
                    const metaData = await metaRes.json();

                    const metadata = {
                        title: metaData.Title || title,
                        poster: metaData.Poster && metaData.Poster !== 'N/A' ? metaData.Poster : null,
                        imdbRating: metaData.imdbRating || 'N/A',
                        genre: metaData.Genre || '',
                    };

                    metadataCache.current[title] = metadata;

                    return {
                        filename,
                        ...metadata,
                    };
                });

                const movieWithMetadata = await Promise.all(metadataPromises);
                setMovies(movieWithMetadata);
            } catch (err) {
                console.error('Error fetching movies or metadata:', err);
            }
        };

        fetchMovies();
    }, []);

    // Unique genre list
    const genres = Array.from(
        new Set(movies.flatMap((m) => m.genre?.split(', ').filter(Boolean)))
    );

    // Combined filter logic
    const filteredMovies = movies.filter((movie) => {
        const matchesSearch =
            movie.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            movie.genre?.toLowerCase().includes(searchTerm.toLowerCase());

        const matchesGenre = selectedGenre
            ? movie.genre?.includes(selectedGenre)
            : true;

        return matchesSearch && matchesGenre;
    });

    return (
        <div className="movie-row">
            <h3 className="msTitel">{title}</h3>
            {/* Search */}
            <div className='searchBox'>
                <div className="sokDiv">
                    <input
                        type="text"
                        placeholder="Search movie titles"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="msSearch"
                    />
                </div>
                {/* Genre Buttons */}
                <div className="centreraCards">
                    <div className="genre-buttons">
                        <button
                            onClick={() => setSelectedGenre('')}
                            className={!selectedGenre ? 'active' : ''}
                        >
                            All
                        </button>
                        {genres.map((genre, index) => (
                            <button
                                key={index}
                                onClick={() => setSelectedGenre(genre)}
                                className={selectedGenre === genre ? 'active' : ''}
                            >
                                {genre}
                            </button>
                        ))}
                    </div>
                </div>
            </div>
            {/* Movie Cards */}
            <div className='centreraCards'>
                <div className="cards">
                    {filteredMovies.map((movie, idx) => (
                        <div
                            className="movie-card"
                            key={`${title}-${movie.filename}`}
                            onClick={() => setSelectedMovie({ filename: movie.filename })}
                        >
                            <div className="hover-area">
                                {movie.poster && (
                                    <img
                                        src={`/moviePosters/${encodeURIComponent(movie.title)}.jpg`}
                                        alt={movie.title}
                                    />

                                )}
                                <span className="movieTitle">
                                    {movie.title} ⭐{movie.imdbRating}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

// Video Overlay
function VideoOverlay({ filename, onClose }) {
    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/movie/${filename}?token=${token}`

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>✖</button>
                <video
                    controls
                    autoPlay
                    className="video-player"
                >
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}

export default Movies;
