import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const API_URL = process.env.REACT_APP_API_URL;

function RecentlyAddedRow({ title, setSelectedMovie }) {
    const [items, setItems] = useState([]);
    const navigate = useNavigate();
    const metadataCache = useRef({});

    useEffect(() => {
        const fetchRecent = async () => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`${API_URL}/api/recent-content`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const data = await res.json();

                const enriched = await Promise.all(data.map(async (item) => {
                    const cacheKey = item.title;

                    if (metadataCache.current[cacheKey]) {
                        return {
                            ...item,
                            ...metadataCache.current[cacheKey],
                        };
                    }

                    const metaRes = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(cacheKey)}`);
                    const meta = await metaRes.json();

                    const metadata = {
                        title: meta.Title || item.title,
                        poster: meta.Poster && meta.Poster !== 'N/A' ? meta.Poster : null,
                        imdbRating: meta.imdbRating || 'N/A',
                        genre: meta.Genre || '',
                    };

                    metadataCache.current[cacheKey] = metadata;

                    return {
                        ...item,
                        ...metadata,
                    };
                }));

                setItems(enriched);
            } catch (err) {
                console.error('Failed to load recent items:', err);
            }
        };

        fetchRecent();
    }, []);

    return (
        <div className='homeComponent'>
            <h3>{title}</h3>
            <div className="cards" id='homeCards'>
                {items.map(item => (
                    <div
                        key={`${item.type}-${item.title}`}
                        className="movie-card"
                        onClick={() => {
                            if (item.type === 'movie') {
                                setSelectedMovie({ filename: item.fileName });
                            } else {
                                navigate(`/series?name=${encodeURIComponent(item.seriesName)}`);
                            }
                        }}
                    >
                        <div className="hover-area">
                            {item.poster && (
                                <img src={item.poster} alt={item.title} className="movie-poster" />
                            )}
                            <span className='movieTitle'>{item.title} ‚≠ê{item.imdbRating}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default RecentlyAddedRow;
