// src/authContext.js
import { createContext, useContext, useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from './Api';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const location = useLocation(); // ðŸ” get current route
  const [user, setUser] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const refreshTimeout = useRef();
  const [loading, setLoading] = useState(true);

  // ðŸ” Refresh token only on non-public routes
  useEffect(() => {
    const publicPaths = ['/', '/about', '/contact']; // add more if needed
    if (!publicPaths.includes(location.pathname)) {
      refreshAccessToken();
    } else {
      setLoading(false); // âœ… don't wait for token on public pages
    }
  }, [location.pathname]);

  const refreshAccessToken = async () => {
    try {
      const res = await api.post('/api/token', {}, { withCredentials: true });
      const newToken = res.data.accessToken;
      const decoded = jwtDecode(newToken);

      setUser(decoded);
      setAccessToken(newToken);
      setIsLoggedIn(true);

      const timeout = (decoded.exp - Date.now() / 1000) * 1000;
      clearTimeout(refreshTimeout.current);
      refreshTimeout.current = setTimeout(refreshAccessToken, timeout - 2000);
    } catch (err) {
      logout(false); // don't clear loading here
    } finally {
      setLoading(false); // âœ… done checking
    }
  };

  const login = (token) => {
    const decoded = jwtDecode(token);
    setUser(decoded);
    setAccessToken(token);
    setIsLoggedIn(true);

    const timeout = (decoded.exp - Date.now() / 1000) * 1000;
    clearTimeout(refreshTimeout.current);
    refreshTimeout.current = setTimeout(refreshAccessToken, timeout - 2000);
  };

  const logout = async (resetLoading = true) => {
    try {
      await api.post('/api/logout');
    } catch (err) {
      console.error('Logout error:', err);
    }
    setUser(null);
    setAccessToken(null);
    setIsLoggedIn(false);
    clearTimeout(refreshTimeout.current);
    if (resetLoading) setLoading(false);
  };

  return (
    <AuthContext.Provider value={{ user, accessToken, isLoggedIn, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
