import { useState } from 'react';
import { Link, useNavigate, NavLink } from 'react-router-dom';
import { useAuth } from '../authContext';

function Header() {
  const navigate = useNavigate();
  const { isLoggedIn, user, logout } = useAuth();
  const [menuOpen, setMenuOpen] = useState(false);

  
const handleNavClick = (path) => {
  setMenuOpen(false); // triggers the animation
  setTimeout(() => {
    navigate(path);    // wait for the animation to play
  }, 300); // same duration as CSS transition
};
  const closeMenuWithAnimation = () => {
  setMenuOpen(false);
};

  const toggleMenu = () => setMenuOpen(prev => !prev);

  return (
    <header className={`header ${menuOpen ? 'open' : 'closed'}`}>
      <Link className='logga' to="/">
        Erkflix
      </Link>
      {isLoggedIn && (
        <>
<button className="nav" onClick={() => handleNavClick('/')}>Home</button>
<button className="nav" onClick={() => handleNavClick('/wish')}>Wishlist</button>
<button className="nav" onClick={() => handleNavClick('/movies')}>Movies</button>
<button className="nav" onClick={() => handleNavClick('/series')}>Series</button>
        </>
      )}
      <div className='signDiv'>
        {isLoggedIn ? (
          <>
            {/* Hamburger button for mobile */}
            <button className={`menuButton ${menuOpen ? 'open' : ''}`} onClick={toggleMenu}>
              <span></span>
              <span></span>
              <span></span>
            </button>
            {user?.role === 'admin' && (
              <Link className="adminKnapp" to="/admin" onClick={() => setMenuOpen(false)}>Admin</Link>
            )}
            <Link className="signinKnapp" to="/" onClick={() => { logout(); setMenuOpen(false); navigate('/'); }}>Sign out</Link>
          </>
        ) : (
          <Link className="signinKnapp" to="/login" onClick={() => setMenuOpen(false)}>Sign in</Link>
        )}
      </div>
    </header>
  );
}

export default Header;
