// src/components/SeriesPage.js
import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import { useParams } from 'react-router-dom';
const { seriesName } = useParams();

const API_URL = process.env.REACT_APP_API_URL;

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

function SeriesPage() {
    const query = useQuery();
    const seriesName = query.get('name');
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [metadata, setMetadata] = useState(null);
    const [episodes, setEpisodes] = useState([]);
    const [availableSeasons, setAvailableSeasons] = useState([]);
    const [currentSeason, setCurrentSeason] = useState('');

    useEffect(() => {
        if (seriesName) {
            axios.get(`${API_URL}/api/series/${encodeURIComponent(seriesName)}/metadata`)
                .then(res => setMetadata(res.data));

            axios.get(`${API_URL}/api/series/${encodeURIComponent(seriesName)}/seasons`)
                .then(res => {
                    const seasons = Object.keys(res.data);
                    setAvailableSeasons(seasons);
                    setCurrentSeason(seasons[0]);
                    setEpisodes(res.data[seasons[0]]);
                });
        }
    }, [seriesName]);

    useEffect(() => {
        if (currentSeason) {
            axios.get(`${API_URL}/api/series/${encodeURIComponent(seriesName)}/seasons`)
                .then(res => setEpisodes(res.data[currentSeason]));
        }
    }, [currentSeason]);

    return (
        <>
            <Header />
            <div className="mainDiv">
                <h3 className="msTitel">{metadata?.Title || seriesName} {currentSeason}</h3>
                <h3 className="msTitel" id="imdbRating">⭐{metadata?.imdbRating}</h3>

                <div className="centreraCards">
                    <div className="season-buttons">
                        {availableSeasons.map(season => (
                            <button
                                key={season}
                                onClick={() => setCurrentSeason(season)}
                                className={currentSeason === season ? 'active' : ''}
                            >
                                {season}
                            </button>
                        ))}
                    </div>
                </div>

                <div className="centreraCards">
                    <div className="cards" id="episodeCards">
                        {episodes
                            .sort((a, b) => {
                                const getEpNum = (filename) => {
                                    const match = filename.match(/^(\d+)\./);
                                    return match ? parseInt(match[1], 10) : 0;
                                };
                                return getEpNum(a.filename) - getEpNum(b.filename);
                            })
                            .map((episode) => (
                                <div
                                    className="movie-card"
                                    key={episode.filename}
                                    onClick={() => {
                                        setSelectedMovie({
                                            filename: episode.filename,
                                            path: episode.path,
                                            seriesName: seriesName
                                        });
                                    }}
                                >
                                    <div className="hover-area">
                                        <div className="image-container">
                                            <img
                                                src={`${API_URL}/seriesPosters/${encodeURIComponent(seriesName)}.jpg`}
                                                alt={seriesName}
                                            />
                                            <span className="movieTitle">{episode.title}</span>
                                        </div>
                                    </div>
                                </div>
                            ))}
                    </div>
                </div>

                {selectedMovie && (
                    <VideoOverlay
                        path={selectedMovie.path}
                        onClose={() => setSelectedMovie(null)}
                    />
                )}
            </div>
        </>
    );
}

function VideoOverlay({ path = '', onClose }) {
    if (!path) return null;

    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/file?path=${encodeURIComponent(path)}&token=${token}`;

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button
                    className="close-button"
                    onClick={onClose}
                >
                    ✖
                </button>
                <video controls autoPlay className="video-player">
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}

export default SeriesPage;
