import { useAuth } from './authContext';
import React, { useEffect, useState, useRef } from 'react';
import Header from './Header';
import movieTitles from './moviesTitles.json';

const API_URL = process.env.REACT_APP_API_URL;

function Movies() {
    const { isLoggedIn } = useAuth();
    const [selectedMovie, setSelectedMovie] = useState(null);

    return (
        <>
            <Header />
            <div className='mainDiv'>
                {isLoggedIn && (
                    <>
                        <MovieRow
                            title="Movies"
                            selectedMovie={selectedMovie}
                            setSelectedMovie={setSelectedMovie}
                        />
                    </>
                )}

                {selectedMovie && (
                    <VideoOverlay
                        filename={selectedMovie.filename}
                        onClose={() => setSelectedMovie(null)}
                    />
                )}
            </div>
        </>
    );
}

function MovieRow({ title, setSelectedMovie }) {
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('');
    const metadataCache = useRef({});

    useEffect(() => {
        const fetchMovies = async () => {
            const metadataPromises = filenames
            .filter((filename) => movieTitles[filename])
            .map(async (filename) => {
              const searchTitle = movieTitles[filename];
          
              // Check cache
              if (metadataCache.current[searchTitle]) {
                return {
                  filename,
                  ...metadataCache.current[searchTitle],
                };
              }
          
              // Fetch and cache
              const metaRes = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(searchTitle)}`);
              const metaData = await metaRes.json();
          
              const metadata = {
                title: metaData.Title || searchTitle,
                poster: metaData.Poster && metaData.Poster !== 'N/A' ? metaData.Poster : null,
                imdbRating: metaData.imdbRating || 'N/A',
                genre: metaData.Genre || '',
              };
          
              metadataCache.current[searchTitle] = metadata;
              return {
                filename,
                ...metadata,
              };
            });          
        };
      
        fetchMovies();
      }, []);

    // Unique genre list
    const genres = Array.from(
        new Set(movies.flatMap((m) => m.genre?.split(', ').filter(Boolean)))
    );

    // Combined filter logic
    const filteredMovies = movies.filter((movie) => {
        const matchesSearch =
            movie.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            movie.genre?.toLowerCase().includes(searchTerm.toLowerCase());

        const matchesGenre = selectedGenre
            ? movie.genre?.includes(selectedGenre)
            : true;

        return matchesSearch && matchesGenre;
    });

    return (
        <div className="movie-row">
            <h3 className="msTitel">{title}</h3>
            {/* Search */}
            <div className='searchBox'>
                <div className="sokDiv">
                    <input
                        type="text"
                        placeholder="Search titles or genres..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="msSearch"
                    />
                </div>
                {/* Genre Buttons */}
                <div className="centreraCards">
                    <div className="genre-buttons">
                        <button
                            onClick={() => setSelectedGenre('')}
                            className={!selectedGenre ? 'active' : ''}
                        >
                            All
                        </button>
                        {genres.map((genre, index) => (
                            <button
                                key={index}
                                onClick={() => setSelectedGenre(genre)}
                                className={selectedGenre === genre ? 'active' : ''}
                            >
                                {genre}
                            </button>
                        ))}
                    </div>
                </div>
            </div>
            {/* Movie Cards */}
            <div className='centreraCards'>
                <div className="cards">
                    {filteredMovies.map((movie, idx) => (
                        <div
                            className="movie-card"
                            key={`${title}-${movie.filename}`}
                            onClick={() => setSelectedMovie({ filename: movie.filename })}
                        >
                            {movie.poster && (
                                <img src={movie.poster} alt={movie.title} className="movie-poster" />
                            )}
                            <span className="movieTitle">
                                {movie.title} ⭐{movie.imdbRating}
                            </span>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

// Video Overlay
function VideoOverlay({ filename, onClose }) {
    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/movie/${filename}?token=${token}`

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>✖</button>
                <video
                    controls
                    autoPlay
                    className="video-player"
                >
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}

export default Movies;
