// src/components/MoviePage.js
import { useAuth } from './authContext';
import { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Header from './Header';

const API_URL = process.env.REACT_APP_API_URL;

export default function MoviePage() {
    const { isLoggedIn } = useAuth();
    const { movieFolder } = useParams();
    const [metadata, setMetadata] = useState(null);
    const [activeTab, setActiveTab] = useState('Details');
    const [suggestedMovies, setSuggestedMovies] = useState([]);
    const metadataCache = useRef({});
    const navigate = useNavigate();

    console.log('üß© movieFolder param:', movieFolder);

    useEffect(() => {
        if (!movieFolder) return;
        const token = localStorage.getItem('token');

        const fetchMetadata = async () => {
            console.log(`üì° Fetching metadata for: ${movieFolder}`);

            try {
                const res = await fetch(`${API_URL}/api/movie-metadata/${encodeURIComponent(movieFolder)}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                if (!res.ok) throw new Error('Metadata not found');
                const data = await res.json();
                setMetadata(data);
                metadataCache.current[data.Title] = {
                    genre: data.Genre || '',
                };
            } catch (err) {
                console.error('‚ùå Failed to fetch movie metadata:', err);
            }
        };

        fetchMetadata();
    }, [movieFolder]);

    useEffect(() => {
        if (!metadata?.Genre) return;
        const token = localStorage.getItem('token');

        const fetchSuggested = async () => {
            try {
                const res = await fetch(`${API_URL}/api/movies`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const movieList = await res.json();

                const currentGenres = metadata.Genre.split(',').map(g => g.trim().toLowerCase());

                const suggestions = movieList.filter(({ title, folder }) => {
                    const m = metadataCache.current[title];
                    if (!m || folder === movieFolder) return false;
                    const otherGenres = m.genre.split(',').map(g => g.trim().toLowerCase());
                    return currentGenres.some(g => otherGenres.includes(g));
                });

                setSuggestedMovies(suggestions);
            } catch (err) {
                console.error('‚ùå Error fetching suggested movies:', err);
            }
        };

        fetchSuggested();
    }, [metadata, movieFolder]);

    return (
        <>
            <Header />
            <div className="mainDiv">
                {isLoggedIn && (
                    <div className='movie-row' id='movie-row-Home'>
                        <div className="centreraCards">
                            <div className="componentDiv">
                                <h3 className="msTitel">{metadata?.Title || movieFolder} ‚≠ê{metadata?.imdbRating}</h3>
                                <ul className='tablist'>
                                    <li
                                        className={activeTab === 'Suggested' ? 'active' : ''}
                                        onClick={() => setActiveTab('Suggested')}
                                    >
                                        Suggested
                                    </li>
                                    <li
                                        className={activeTab === 'Details' ? 'active' : ''}
                                        onClick={() => setActiveTab('Details')}
                                    >
                                        Details
                                    </li>
                                </ul>

                                {activeTab === 'Suggested' && (
                                    <div className="cards" id='episodeCards'>
                                        {suggestedMovies.length === 0 ? (
                                            <p>No suggestions found.</p>
                                        ) : (
                                            suggestedMovies.map((movie) => (
                                                <div
                                                    key={movie.filename}
                                                    className="movie-card"
                                                    onClick={() => navigate(`/movies/${encodeURIComponent(movie.folder)}`)}
                                                >
                                                    <div className="hover-area">
                                                        <img
                                                            src={`${API_URL}/moviePosters/${encodeURIComponent(movie.folder)}.jpg`}
                                                            alt={movie.title}
                                                        />
                                                        <span className="movieTitle">{movie.title}</span>
                                                    </div>
                                                </div>
                                            ))
                                        )}
                                    </div>
                                )}
                                {activeTab === 'Details' && (
                                    <div className='details'>
                                        <div className="details-wrapper">
                                            <div className='plot'>
                                                <p className='detailsTitle'>Plot:</p><p>{metadata?.Plot}</p>
                                            </div>
                                            <div className='genre'>
                                                <div className='detailsDiv'><p className='detailsTitle'>Released:</p><p>{metadata?.Released}</p></div>
                                                <p className='detailsTitle'>Genre:</p><p>{metadata?.Genre}</p>
                                            </div>
                                            <div className='starring'>
                                                <div className='detailsDiv'><p className='detailsTitle'>Director:</p><p>{metadata?.Director}</p></div>
                                                <div className='detailsDiv'><p className='detailsTitle'>Starring:</p><p>{metadata?.Actors}</p></div>
                                                <div className='detailsDiv'><p className='detailsTitle'>Awards:</p><p>{metadata?.Awards}</p></div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
}
