import React, { useState, useEffect } from 'react';
import Header from './Header';

const API_URL = process.env.REACT_APP_API_URL;

function Wish() {
    const token = localStorage.getItem('token');
    let userEmail = '';
    let userRole = '';

    if (token) {
        try {
            const decoded = JSON.parse(atob(token.split('.')[1]));
            userEmail = decoded.email;
            userRole = decoded.role;
        } catch (err) {
            console.error('Invalid token', err);
        }
    }

    const [title, setTitle] = useState('');
    const [message, setMessage] = useState('');
    const [wishes, setWishes] = useState([]);

    // Fetch all wishes on load
    useEffect(() => {
        fetchWishes();
    }, []);

    const fetchWishes = async () => {
        try {
            const res = await fetch(`${API_URL}/api/wishes`);
            const data = await res.json();
            setWishes(data);
        } catch (err) {
            console.error('Error fetching wishes:', err);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const res = await fetch(`${API_URL}/api/wishes`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ title, message }),
            });

            const data = await res.json();
            if (res.ok) {
                setTitle('');
                setMessage('');
                await fetchWishes();
            } else {
                alert(data.message || 'Error submitting wish');
            }
        } catch (err) {
            console.error('Wish submit error:', err);
            alert('Something went wrong');
        }
    };

    const handleDelete = async (wishId) => {
        const token = localStorage.getItem('token');
        if (!window.confirm('Are you sure you want to delete this Wish?')) return;

        try {
            const res = await fetch(`${API_URL}/api/wishes/delete/${wishId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            await fetchWishes();
        } catch (err) {
            console.error('Error deleting Wish:', err);
        }
    };

    return (
        <div>
            <Header />
            <div className='mainDiv'>
                <div className="form-container">
                    <h2 className='wishTitle'>Make a Wish ✨</h2>
                    <form onSubmit={handleSubmit}>
                        <input className='makeWishTitle'
                            type="text"
                            placeholder="Title"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            required
                        />
                        <textarea
                            placeholder="Message (optional)"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            rows={4}
                        />
                        <button type="submit" className="authKnapp">Submit Wish</button>
                    </form>
                </div>
                <div className='wishList'>
                    <h3 className='wishTitle'>Wishes</h3>
                    {wishes.length === 0 ? (
                        <p>No wishes yet</p>
                    ) : (
                        <div className='wishTableDiv'>
                            <table className='wishTable'>
                                <tbody>
                                    <tr>
                                        <th>Name</th>
                                        <th>Title</th>
                                        <th>Comment</th>
                                        <th></th>
                                    </tr>
                                    {wishes.map((wish, index) => (
                                        <tr className='wishPost' key={index}>
                                            <td className='wishName'>{wish.userName}</td>
                                            <td>{wish.title}</td>
                                            <td>{wish.message || ''}</td>
                                            <td>
                                                {(wish.userEmail === userEmail || userRole === 'admin') && (
                                                    <button className="wishDeleteKnapp" onClick={() => handleDelete(wish._id)}>
                                                        Delete
                                                    </button>
                                                )}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Wish;
