import { useAuth } from './authContext';
import React, { useEffect, useState, useRef } from 'react';
import Header from './Header';
import { useNavigate, useLocation } from 'react-router-dom';
const API_URL = process.env.REACT_APP_API_URL;

function Series() {
    const navigate = useNavigate();
    const location = useLocation();
    const { isLoggedIn } = useAuth();
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [seriesList, setSeriesList] = useState([]);
    const [episodes, setEpisodes] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('');
    const [genres, setGenres] = useState([]);
    const [selectedSeriesMetadata, setSelectedSeriesMetadata] = useState(null);
    const [availableSeasons, setAvailableSeasons] = useState([]);
    const [currentSeason, setCurrentSeason] = useState('');
    const seriesMetadataCache = useRef({});
    const [selectedSeries, setSelectedSeries] = useState(null);

    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const seriesName = params.get('name');
        const play = params.get('play');

        setSelectedSeries(seriesName);

        if (!play && selectedMovie) {
            setSelectedMovie(null);
            return;
        }

        if (play && episodes.length > 0 && !selectedMovie) {
            const episode = episodes.find(e => e.filename === play);
            if (episode) {
                setSelectedMovie(episode);
            }
        }
    }, [location.search, episodes, selectedMovie]);

    useEffect(() => {
        const fetchSeries = async () => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`${API_URL}/api/series`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const folders = await res.json();

                const seriesWithMetadata = await Promise.all(
                    folders.map(async (folder) => {

                        const metaRes = await fetch(`${API_URL}/api/series-metadata/${encodeURIComponent(folder)}`, {
                            headers: { Authorization: `Bearer ${token}` },
                        });
                        const metaData = await metaRes.json();

                        return {
                            folder,
                            poster: metaData.Poster && metaData.Poster !== 'N/A' ? metaData.Poster : null,
                            title: folder,
                            imdbRating: metaData.imdbRating || 'N/A',
                            genre: metaData.Genre || '',
                        };
                    })
                );

                setSeriesList(seriesWithMetadata);

                const allGenres = seriesWithMetadata.flatMap(series =>
                    series.genre.split(',').map(g => g.trim())
                );
                setGenres([...new Set(allGenres)]);
            } catch (err) {
                console.error('Error fetching series folders or metadata:', err);
            }
        };

        fetchSeries();
    }, []);

    // Fetch episodes for selected series
    useEffect(() => {
        if (!selectedSeries) return;

        const fetchEpisodes = async () => {
            const token = localStorage.getItem('token');

            try {
                const res = await fetch(`${API_URL}/api/series/${encodeURIComponent(selectedSeries)}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch episodes');
                }

                const data = await res.json();
                const allEpisodes = [];

                const seasonNames = data.seasons.map((seasonObj) => seasonObj.season);
                setAvailableSeasons(seasonNames);
                setCurrentSeason(seasonNames[0] || ''); // Default to first season

                data.seasons.forEach((seasonObj) => {
                    seasonObj.episodes.forEach((episode) => {
                        allEpisodes.push({
                            filename: episode.filename,
                            seriesName: selectedSeries,
                            season: seasonObj.season,
                            title: episode.filename.replace(/\.[^/.]+$/, ''),
                            path: episode.path,
                        });
                    });
                });

                setEpisodes(allEpisodes);
            } catch (err) {
                console.error('Error fetching episodes:', err);
            }
        };

        fetchEpisodes();
    }, [selectedSeries]);

    useEffect(() => {
        const fetchMetadata = async () => {
            if (!selectedSeries) return;

            // Use cached version if available
            if (seriesMetadataCache.current[selectedSeries]) {
                setSelectedSeriesMetadata(seriesMetadataCache.current[selectedSeries]);
                return;
            }

            try {
                const res = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(selectedSeries)}`);
                const data = await res.json();

                // Cache the result
                seriesMetadataCache.current[selectedSeries] = data;
                setSelectedSeriesMetadata(data);
            } catch (err) {
                console.error('Error fetching selected series metadata:', err);
            }
        };

        fetchMetadata();
    }, [selectedSeries]);

    const filteredSeriesList = seriesList.filter(series => {
        const matchesSearch = series.title.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesGenre = !selectedGenre || (series.genre && series.genre.toLowerCase().includes(selectedGenre.toLowerCase()));
        return matchesSearch && matchesGenre;
    });

    const filteredEpisodes = episodes.filter(episode => {
        const matchesSearch = episode.title.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesSeason = episode.season === currentSeason;
        return matchesSearch && matchesSeason;
    });

    return (
        <>
            <Header />
            <div className="mainDiv">
                {isLoggedIn && (
                    <div className="movie-row">
                        {!selectedSeries ? (
                            <>
                                <h3 className="msTitel">Series</h3>
                                <div className='searchBox'>
                                    <div className="sokDiv">
                                        <input
                                            type="text"
                                            placeholder="Search series..."
                                            value={searchTerm}
                                            onChange={(e) => setSearchTerm(e.target.value)}
                                            className="msSearch"
                                        />
                                    </div>
                                    <div className='centreraCards'>
                                        <div className="genre-buttons">
                                            <button
                                                onClick={() => setSelectedGenre('')}
                                                className={!selectedGenre ? 'active' : ''}
                                            >
                                                All
                                            </button>
                                            {genres.map((genre, idx) => (
                                                <button
                                                    key={idx}
                                                    onClick={() => setSelectedGenre(genre)}
                                                    className={selectedGenre === genre ? 'active' : ''}
                                                >
                                                    {genre}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                                <div className='centreraCards'>
                                    <div className="cards">
                                        {filteredSeriesList.map(series => (
                                            <div
                                                key={series.folder}
                                                className="movie-card"
                                                onClick={() => {
                                                    setSelectedSeries(series.folder);
                                                    navigate(`/series?name=${encodeURIComponent(series.folder)}`);
                                                }}
                                            >
                                                <div class="hover-area">
                                                    {series.poster && (
                                                        <img
                                                            src={`${API_URL}/seriesPosters/${encodeURIComponent(series.title)}.jpg`}
                                                            alt={series.title}
                                                        />
                                                    )}
                                                    <span className="movieTitle">{series.title} ⭐{series.imdbRating}</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </>
                        ) : (
                            <>
                                <h3 id='episodeTitel' className="msTitel">{selectedSeries} {currentSeason}</h3>
                                <div className='centreraCards'>
                                    <div className="season-buttons">
                                        {availableSeasons.map((season) => (
                                            <button
                                                key={season}
                                                onClick={() => setCurrentSeason(season)}
                                                className={currentSeason === season ? 'active' : ''}
                                            >
                                                {season}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                                <div className='centreraCards'>
                                    <div className="cards">
                                        {filteredEpisodes.map((episode) => (
                                            <div
                                                className="movie-card"
                                                key={`${episode.seriesName}-${episode.filename}`}
                                                onClick={() => {
                                                    navigate(`/series?name=${encodeURIComponent(episode.seriesName)}&play=${encodeURIComponent(episode.filename)}`);
                                                    setSelectedMovie({
                                                        filename: episode.filename,
                                                        path: episode.path,
                                                        seriesName: episode.seriesName
                                                    });
                                                }}
                                            >
                                                <div className="hover-area">
                                                    {selectedSeriesMetadata?.Poster && (
                                                        <img
                                                            src={`/seriesPosters/${encodeURIComponent(selectedSeriesMetadata?.Title || selectedSeries)}.jpg`}
                                                            alt={selectedSeriesMetadata?.Title || selectedSeries}
                                                        />
                                                    )}
                                                    <span className="movieTitle">
                                                        {episode.title} ⭐{selectedSeriesMetadata?.imdbRating || 'N/A'}
                                                    </span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </>
                        )}
                    </div>
                )}
                {selectedMovie && (
                    <VideoOverlay
                        path={selectedMovie.path}
                        onClose={() => {
                            setSelectedMovie(null);
                            navigate(`/series?name=${encodeURIComponent(selectedMovie.seriesName)}`);
                        }}
                    />
                )}
            </div>
        </>
    );
}

function VideoOverlay({ path = '', onClose }) {
    if (!path) return null;

    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/file?path=${encodeURIComponent(path)}&token=${token}`;

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>✖</button>
                <video controls autoPlay className="video-player">
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}


export default Series;
