import React, { useEffect, useState, useRef } from 'react';

const API_URL = process.env.REACT_APP_API_URL;

function MovieRow({ title, setSelectedMovie, topPicks = false }) {
    const [movies, setMovies] = useState([]);
    const metadataCache = useRef({});

    useEffect(() => {
        const fetchMovies = async () => {
            const token = localStorage.getItem('token');
            try {
                let movieList = [];

                if (topPicks) {
                    const res = await fetch(`${API_URL}/api/top-picks`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    movieList = await res.json(); // [{ filename, title, folder }]
                } else {
                    const res = await fetch(`${API_URL}/api/movies`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    movieList = await res.json(); // [{ filename, title, folder }]
                }

                const metadataPromises = movieList.map(async ({ filename, title, folder }) => {
                    if (metadataCache.current[title]) {
                        return {
                            filename,
                            folder,
                            ...metadataCache.current[title],
                        };
                    }

                    const metaRes = await fetch(`${API_URL}/api/metadata/${encodeURIComponent(title)}`);
                    const metaData = await metaRes.json();

                    const metadata = {
                        title: metaData.Title || title,
                        poster: metaData.Poster && metaData.Poster !== 'N/A' ? metaData.Poster : null,
                        imdbRating: metaData.imdbRating || 'N/A',
                        genre: metaData.Genre || '',
                    };

                    metadataCache.current[title] = metadata;

                    return {
                        filename,
                        folder,
                        ...metadata,
                    };
                });

                const movieWithMetadata = await Promise.all(metadataPromises);
                setMovies(movieWithMetadata);
            } catch (err) {
                console.error('Error fetching movies or metadata:', err);
            }
        };

        fetchMovies();
    }, [topPicks]);

    return (
        <div className='centreraCards'>
            <div className='homeComponent'>
                <h3>{title}</h3>
                <div className="cards" id='homeCards'>
                    {movies.map((movie) => (
                        <div
                            className="movie-card"
                            key={`${title}-${movie.filename}`}
                            onClick={() => setSelectedMovie({ filename: movie.filename, folder: movie.folder })}
                        >
                            <div className="hover-area">
                                {movie.poster && (
                                    <img
                                        src={`${API_URL}/moviePosters/${encodeURIComponent(movie.folder)}.jpg`}
                                        alt={movie.title}
                                    />
                                )}
                                <span className='movieTitle'>{movie.title} ‚≠ê{movie.imdbRating}</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default MovieRow;
