// src/components/Watch.js
import React, { useEffect, useState, useRef } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import Header from '../Components/Header';
import api from '../Api';

export default function Watch() {
  const [searchParams] = useSearchParams();
  const path = searchParams.get('path');
  const movieFolder = searchParams.get('movie');
  const navigate = useNavigate();

  const [metadata, setMetadata] = useState(null);
  const [suggestedMovies, setSuggestedMovies] = useState([]);
  const metadataCache = useRef({});

  // Fetch movie or series metadata
  useEffect(() => {
    if (!path && !movieFolder) return;

    const fetchMetadata = async () => {
      try {
        if (movieFolder) {
          const res = await api.get(`/api/movie-metadata/${encodeURIComponent(movieFolder)}`);
          setMetadata(res.data);
          metadataCache.current[res.data.Title] = { genre: res.data.Genre || '' };
        }
      } catch (err) {
        console.error('❌ Failed to fetch metadata:', err);
      }
    };

    fetchMetadata();
  }, [movieFolder, path]);

  // Fetch suggested movies if watching a movie
  useEffect(() => {
    if (!metadata?.Genre || !movieFolder) return;

    fetchSuggested();
  }, [metadata, movieFolder]);

  const getVideoUrl = () => {
    if (path) {
      return `${api.defaults.baseURL}/api/stream/file?path=${encodeURIComponent(path)}`;
    }
    if (movieFolder && metadata?.filename) {
      return `${api.defaults.baseURL}/api/stream/movie/${encodeURIComponent(movieFolder)}/${encodeURIComponent(metadata.filename)}`;
    }
    return '';
  };

  const title = metadata?.Title || path || movieFolder;

  return (
    <>
      <Header />
      <div className="mainDiv">
        <div className="video-wrapper">
          <button className="close-button" onClick={() => navigate(-1)}>✖</button>
          <h2 className="msTitel">{title} ⭐{metadata?.imdbRating}</h2>
          <video controls autoPlay className="video-player">
            <source src={getVideoUrl()} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>

        {movieFolder && (
          <>
            <ul className='tablist'>
              <li className="active">Suggested</li>
            </ul>
            <div className="cards" id="suggestedCards">
              {suggestedMovies.length === 0 ? (
                <p>No suggestions found.</p>
              ) : (
                suggestedMovies.map((movie) => (
                  <div
                    key={movie.filename}
                    className="movie-card"
                    onClick={() => navigate(`/movies/${encodeURIComponent(movie.folder)}`)}
                  >
                    <div className="hover-area">
                      <img
                        src={`${api.defaults.baseURL}/moviePosters/${encodeURIComponent(movie.folder)}.jpg`}
                        alt={movie.title}
                      />
                    </div>
                  </div>
                ))
              )}
            </div>
          </>
        )}
      </div>
    </>
  );
}
