// src/components/SeriesPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';

const API_URL = process.env.REACT_APP_API_URL;

export default function SeriesPage() {
    const { seriesName } = useParams();
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [metadata, setMetadata] = useState(null);
    const [episodes, setEpisodes] = useState([]);
    const [availableSeasons, setAvailableSeasons] = useState([]);
    const [currentSeason, setCurrentSeason] = useState('');

    // Store all episodes grouped by season
    const [seasonMap, setSeasonMap] = useState({});

    useEffect(() => {
        if (!seriesName) return;

        const fetchMetadata = async () => {
            try {
                const res = await fetch(`${API_URL}/api/series-metadata/${encodeURIComponent(seriesName)}`);
                if (!res.ok) throw new Error('Metadata not found');
                const data = await res.json();
                setMetadata(data);
            } catch (err) {
                console.error('❌ Failed to fetch series metadata:', err);
            }
        };

        const fetchSeasons = async () => {
            try {
                const res = await fetch(`${API_URL}/api/series/${encodeURIComponent(seriesName)}`);
                if (!res.ok) throw new Error('Seasons not found');
                const data = await res.json();

                const grouped = {};
                for (const { season, episodes } of data.seasons) {
                    grouped[season] = episodes;
                }

                setSeasonMap(grouped);

                const allSeasons = Object.keys(grouped);
                setAvailableSeasons(allSeasons);
                setCurrentSeason(allSeasons[0]);
                setEpisodes(grouped[allSeasons[0]]);
            } catch (err) {
                console.error('❌ Failed to fetch seasons:', err);
            }
        };

        fetchMetadata();
        fetchSeasons();
    }, [seriesName]);

    useEffect(() => {
        if (currentSeason && seasonMap[currentSeason]) {
            setEpisodes(seasonMap[currentSeason]);
        }
    }, [currentSeason, seasonMap]);

    return (
        <>
            <Header />
            <div className="mainDiv">
                <h3 className="msTitel">{metadata?.Title || seriesName} {currentSeason}</h3>
                <h3 className="msTitel" id="imdbRating">⭐{metadata?.imdbRating}</h3>

                <div className="centreraCards">
                    <div className="season-buttons">
                        {availableSeasons.map(season => (
                            <button
                                key={season}
                                onClick={() => setCurrentSeason(season)}
                                className={currentSeason === season ? 'active' : ''}
                            >
                                {season}
                            </button>
                        ))}
                    </div>
                </div>

                <div className="centreraCards">
                    <div className="cards" id="episodeCards">
                        {episodes
                            .sort((a, b) => {
                                const getEpNum = (filename) => {
                                    const match = filename.match(/^(\d+)\./);
                                    return match ? parseInt(match[1], 10) : 0;
                                };
                                return getEpNum(a.filename) - getEpNum(b.filename);
                            })
                            .map((episode) => (
                                <div
                                    className="movie-card"
                                    key={episode.filename}
                                    onClick={() => {
                                        setSelectedMovie({
                                            filename: episode.filename,
                                            path: episode.path,
                                            seriesName,
                                        });
                                    }}
                                >
                                    <div className="hover-area">
                                        <div className="image-container">
                                            <img
                                                src={`${API_URL}/seriesPosters/${encodeURIComponent(seriesName)}.jpg`}
                                                alt={seriesName}
                                            />
                                            <span className="movieTitle">{episode.filename}</span>
                                        </div>
                                    </div>
                                </div>
                            ))}
                    </div>
                </div>

                {selectedMovie && (
                    <VideoOverlay
                        path={selectedMovie.path}
                        onClose={() => setSelectedMovie(null)}
                    />
                )}
            </div>
        </>
    );
}

function VideoOverlay({ path = '', onClose }) {
    if (!path) return null;

    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/file?path=${encodeURIComponent(path)}&token=${token}`;

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>✖</button>
                <video controls autoPlay className="video-player">
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}
