import { useState } from 'react';
import { Link, useNavigate, NavLink } from 'react-router-dom';
import { useAuth } from '../authContext';

function Header() {
  const navigate = useNavigate();
  const { isLoggedIn, user, logout } = useAuth();
  const [menuOpen, setMenuOpen] = useState(false);

  
const handleNavClick = (path) => {
  setMenuOpen(false); // triggers the animation
  setTimeout(() => {
    navigate(path);
  }, 200); // same duration as CSS transition
};

  const toggleMenu = () => setMenuOpen(prev => !prev);

  return (
    <header className={`header ${menuOpen ? 'open' : 'closed'}`}>
      <Link className='logga' to="/">
        Erkflix
      </Link>
      {isLoggedIn && (
        <>
          <NavLink to="/" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`} onClick={(e) => handleNavClick(e, '/')}>Home</NavLink>
          <NavLink to="/wish" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`} onClick={(e) => handleNavClick(e, '/wish')}>Wishlist</NavLink>
          <NavLink to="/movies" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`} onClick={(e) => handleNavClick(e, '/movies')}>Movies</NavLink>
          <NavLink to="/series" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`} onClick={(e) => handleNavClick(e, '/series')}>Series</NavLink>
        </>
      )}
      <div className='signDiv'>
        {isLoggedIn ? (
          <>
            {/* Hamburger button for mobile */}
            <button className={`menuButton ${menuOpen ? 'open' : ''}`} onClick={toggleMenu}>
              <span></span>
              <span></span>
              <span></span>
            </button>
            {user?.role === 'admin' && (
              <Link className="adminKnapp" to="/admin" onClick={() => setMenuOpen(false)}>Admin</Link>
            )}
            <Link className="signinKnapp" to="/" onClick={() => { logout(); setMenuOpen(false); navigate('/'); }}>Sign out</Link>
          </>
        ) : (
          <Link className="signinKnapp" to="/login" onClick={() => setMenuOpen(false)}>Sign in</Link>
        )}
      </div>
    </header>
  );
}

export default Header;
