// src/components/MoviePage.js
import { useAuth } from '../authContext';
import { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Header from '../Components/Header';

const API_URL = process.env.REACT_APP_API_URL;

export default function MoviePage() {
    const { isLoggedIn } = useAuth();
    const { movieFolder } = useParams();
    const [metadata, setMetadata] = useState(null);
    const [activeTab, setActiveTab] = useState('Suggested');
    const [suggestedMovies, setSuggestedMovies] = useState([]);
    const metadataCache = useRef({});
    const navigate = useNavigate();
    const [showPlayer, setShowPlayer] = useState(false);

    useEffect(() => {
        if (!movieFolder) return;
        const token = localStorage.getItem('token');

        const fetchMetadata = async () => {
            try {
                const res = await fetch(`${API_URL}/api/movie-metadata/${encodeURIComponent(movieFolder)}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                if (!res.ok) throw new Error('Metadata not found');
                const data = await res.json();
                setMetadata(data);
                metadataCache.current[data.Title] = {
                    genre: data.Genre || '',
                };
            } catch (err) {
                console.error('❌ Failed to fetch movie metadata:', err);
            }
        };

        fetchMetadata();
    }, [movieFolder]);

    useEffect(() => {
        if (!metadata?.Genre) return;
        const token = localStorage.getItem('token');

        const fetchSuggested = async () => {
            try {
                const res = await fetch(`${API_URL}/api/movies`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const movieList = await res.json();

                const metaRes = await fetch(`${API_URL}/api/movie-metadata`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const allMetadata = await metaRes.json();

                // Fill the metadataCache
                Object.values(allMetadata).forEach((meta) => {
                    metadataCache.current[meta.Title] = {
                        genre: meta.Genre || '',
                    };
                });

                const currentGenres = metadata.Genre.split(',').map((g) => g.trim().toLowerCase());

                const suggestions = movieList.filter(({ title, folder }) => {
                    const m = metadataCache.current[title];
                    if (!m || folder === movieFolder) return false;
                    const otherGenres = m.genre.split(',').map((g) => g.trim().toLowerCase());
                    return currentGenres.some((g) => otherGenres.includes(g));
                });

                setSuggestedMovies(suggestions);
            } catch (err) {
                console.error('❌ Error fetching suggested movies:', err);
            }
        };

        fetchSuggested();
    }, [metadata, movieFolder]);

    return (
        <>
            <Header />
            <div className="mainDiv">
                {isLoggedIn && (
                    <div className='movie-row' id='movie-row-Home'>
                        <div className="centreraCards">
                            <div className="componentDiv">
                                <h3 className="msTitel">{metadata?.Title || movieFolder} ⭐{metadata?.imdbRating}</h3>
                                <button className="play-button" onClick={() => setShowPlayer(true)}><p>▶</p><p>Play</p></button>
                                <ul className='tablist'>
                                    <li
                                        className={activeTab === 'Suggested' ? 'active' : ''}
                                        onClick={() => setActiveTab('Suggested')}
                                    >
                                        Suggested
                                    </li>
                                    <li
                                        className={activeTab === 'Details' ? 'active' : ''}
                                        onClick={() => setActiveTab('Details')}
                                    >
                                        Details
                                    </li>
                                </ul>

                                {activeTab === 'Suggested' && (
                                    <div className="cards" id='suggestedCards'>
                                        {suggestedMovies.length === 0 ? (
                                            <p>No suggestions found.</p>
                                        ) : (
                                            suggestedMovies.map((movie) => (
                                                <div
                                                    key={movie.filename}
                                                    className="movie-card"
                                                    onClick={() => navigate(`/movies/${encodeURIComponent(movie.folder)}`)}
                                                >
                                                    <div className="hover-area">
                                                        <img
                                                            src={`${API_URL}/moviePosters/${encodeURIComponent(movie.folder)}.jpg`}
                                                            alt={movie.title}
                                                        />
                                                    </div>
                                                </div>
                                            ))
                                        )}
                                    </div>
                                )}
                                {activeTab === 'Details' && (
                                    <div className='details'>
                                        <div className="details-wrapper">
                                            <div className='plot'>
                                                <p className='detailsTitle'>Plot:</p><p>{metadata?.Plot}</p>
                                            </div>
                                            <div className='genre'>
                                                <div className='detailsDiv'><p className='detailsTitle'>Released:</p><p>{metadata?.Released}</p></div>
                                                <p className='detailsTitle'>Genre:</p><p>{metadata?.Genre}</p>
                                            </div>
                                            <div className='starring'>
                                                <div className='detailsDiv'><p className='detailsTitle'>Director:</p><p>{metadata?.Director}</p></div>
                                                <div className='detailsDiv'>
                                                    <p className='detailsTitle'>Starring:</p>
                                                    <div>
                                                        {metadata?.Actors?.split(',').map((actor, i) => (
                                                            <p key={i}>{actor.trim()}</p>
                                                        ))}
                                                    </div>
                                                </div>
                                                <div className='detailsDiv'><p className='detailsTitle'>Awards:</p><p>{metadata?.Awards}</p></div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                )}
                {showPlayer && (
                    <VideoOverlay
                        folder={movieFolder}
                        filename={metadata?.filename}
                        onClose={() => setShowPlayer(false)}
                    />
                )}
            </div>
        </>
    );
}

function VideoOverlay({ folder = '', filename = '', onClose }) {
    if (!folder || !filename) return null;

    const token = localStorage.getItem('token');
    const videoUrl = `${process.env.REACT_APP_API_URL}/api/stream/movie/${encodeURIComponent(folder)}/${encodeURIComponent(filename)}?token=${token}`;

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>✖</button>
                <video controls autoPlay className="video-player">
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}
