// src/components/SeriesPage.js
import { useAuth } from '../authContext';
import { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../Components/Header';
import api from '../Api';

export default function SeriesPage() {
    const { isLoggedIn } = useAuth();
    const { seriesName } = useParams();
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [metadata, setMetadata] = useState(null);
    const [episodes, setEpisodes] = useState([]);
    const [availableSeasons, setAvailableSeasons] = useState([]);
    const [currentSeason, setCurrentSeason] = useState('');
    const [activeTab, setActiveTab] = useState('Episodes');
    const [suggestedSeries, setSuggestedSeries] = useState([]);

    // Store all episodes grouped by season
    const [seasonMap, setSeasonMap] = useState({});

    useEffect(() => {
        if (!seriesName) return;

        const fetchMetadata = async () => {
            try {
                const res = await api.get(`/api/series-metadata/${encodeURIComponent(seriesName)}`);
                const data = res.data;
                setMetadata(data);
                if (!res.ok) throw new Error('Metadata not found');
                setMetadata(data);
            } catch (err) {
                console.error('❌ Failed to fetch series metadata:', err);
            }
        };

        const fetchSeasons = async () => {
            try {
                const res = await fetch(`${API_URL}/api/series/${encodeURIComponent(seriesName)}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (!res.ok) throw new Error('Seasons not found');
                const data = await res.json();

                const grouped = {};
                for (const { season, episodes } of data.seasons) {
                    grouped[season] = episodes;
                }

                const allSeasons = Object.keys(grouped);
                setSeasonMap(grouped);
                setAvailableSeasons(allSeasons);
                setCurrentSeason(allSeasons[0]);
                setEpisodes(grouped[allSeasons[0]]);
            } catch (err) {
                console.error('❌ Failed to fetch seasons:', err);
            }
        };

        fetchMetadata();
        fetchSeasons();
    }, [seriesName]);

    useEffect(() => {
        if (!metadata?.Genre) return;

        const token = localStorage.getItem('token');

        const fetchSuggested = async () => {
            try {
                const res = await fetch(`${API_URL}/api/series-metadata`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                const allMetadata = await res.json();

                const currentGenres = metadata.Genre.split(',').map(g => g.trim().toLowerCase());

                const suggestions = Object.entries(allMetadata)
                    .filter(([folder, data]) => {
                        if (folder === seriesName) return false;
                        if (!data.Genre) return false;
                        const otherGenres = data.Genre.split(',').map(g => g.trim().toLowerCase());
                        return currentGenres.some(g => otherGenres.includes(g));
                    })
                    .map(([folder, data]) => ({
                        folder,
                        title: data.Title,
                        poster: data.Poster
                    }));

                setSuggestedSeries(suggestions);
            } catch (err) {
                console.error('❌ Error fetching suggested series:', err);
            }
        };

        fetchSuggested();
    }, [metadata, seriesName]);



    useEffect(() => {
        if (currentSeason && seasonMap[currentSeason]) {
            setEpisodes(seasonMap[currentSeason]);
        }
    }, [currentSeason, seasonMap]);

    function SeasonDropdown({ availableSeasons, currentSeason, setCurrentSeason }) {
        const [isOpen, setIsOpen] = useState(false);
        const dropdownRef = useRef();

        // Close dropdown when clicking outside
        useEffect(() => {
            const handleClickOutside = (event) => {
                if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                    setIsOpen(false);
                }
            };
            document.addEventListener("mousedown", handleClickOutside);
            return () => document.removeEventListener("mousedown", handleClickOutside);
        }, []);

        return (
            <div className="season-dropdown" ref={dropdownRef}>
                <button
                    className="dropdown-toggle"
                    onClick={() => setIsOpen(!isOpen)}
                >
                    <span>{currentSeason}</span>
                    <span>▼</span>
                </button>
                {isOpen && (
                    <div className="dropdown-menu">
                        {availableSeasons.map((season) => (
                            <div
                                key={season}
                                className={`dropdown-item ${currentSeason === season ? 'active' : ''}`}
                                onClick={() => {
                                    setCurrentSeason(season);
                                    setIsOpen(false);
                                }}
                            >
                                {season}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        );
    }

    return (
        <>
            <Header />
            <div className="mainDiv">
                {isLoggedIn && (
                    <div className='movie-row' id='movie-row-Home'>
                        <>
                            <div className="centreraCards">
                                <div className="componentDiv">
                                    <h3 className="msTitel">{metadata?.Title || seriesName} ⭐{metadata?.imdbRating}</h3>
                                    <ul className='tablist'>
                                        <li
                                            className={activeTab === 'Episodes' ? 'active' : ''}
                                            onClick={() => setActiveTab('Episodes')}
                                        >
                                            Episodes
                                        </li>
                                        <li
                                            className={activeTab === 'Suggested' ? 'active' : ''}
                                            onClick={() => setActiveTab('Suggested')}
                                        >
                                            Suggested
                                        </li>
                                        <li
                                            className={activeTab === 'Details' ? 'active' : ''}
                                            onClick={() => setActiveTab('Details')}
                                        >
                                            Details
                                        </li>
                                    </ul>
                                    {activeTab === 'Episodes' && (
                                        <>
                                            <div className="season-select-wrapper">
                                                <SeasonDropdown
                                                    availableSeasons={availableSeasons}
                                                    currentSeason={currentSeason}
                                                    setCurrentSeason={setCurrentSeason}
                                                />
                                            </div>
                                            <div className="cards" id="episodeCards">
                                                {episodes
                                                    .sort((a, b) => {
                                                        const getEpNum = (title) => {
                                                            const match = title.match(/^(\d+)[. -]?/); // Match "01", "1.", "1 -", etc.
                                                            return match ? parseInt(match[1], 10) : 0;
                                                        };
                                                        return getEpNum(a.title) - getEpNum(b.title);
                                                    })
                                                    .map((episode) => (
                                                        <div
                                                            className="movie-card"
                                                            key={episode.path}
                                                            onClick={() => {
                                                                setSelectedMovie({
                                                                    title: episode.title,
                                                                    path: episode.path,
                                                                    seriesName,
                                                                });
                                                            }}
                                                        >
                                                            <div className="hover-area">
                                                                <div className="image-container">
                                                                    <img
                                                                        src={`${API_URL}/seriesPosters/${encodeURIComponent(seriesName)}.jpg`}
                                                                        alt={seriesName}
                                                                    />
                                                                    <span className="movieTitle">{episode.title}</span>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    ))
                                                }
                                            </div>
                                        </>
                                    )}

                                    {activeTab === 'Suggested' && (
                                        <div className="cards" id='suggestedCards'>
                                            {suggestedSeries.length === 0 ? (
                                                <p>No suggestions found.</p>
                                            ) : (
                                                suggestedSeries.map((s) => (
                                                    <div
                                                        key={s.folder}
                                                        className="movie-card"
                                                        onClick={() => window.location.href = `/series/${encodeURIComponent(s.folder)}`}
                                                    >
                                                        <div className="hover-area">
                                                            <img
                                                                src={`${API_URL}/seriesPosters/${encodeURIComponent(s.folder)}.jpg`}
                                                                alt={s.title}
                                                            />
                                                        </div>
                                                    </div>
                                                ))
                                            )}
                                        </div>
                                    )}

                                    {activeTab === 'Details' && (
                                        <div className='details'>
                                            <div className="details-wrapper">
                                                <div className='plot'>
                                                    <p className='detailsTitle'>Plot:</p><p>{metadata?.Plot}</p></div>
                                                <div className='genre'>
                                                    <div className='detailsDiv'><p className='detailsTitle'>Released:</p><p>{metadata?.Released}</p></div>
                                                    <p className='detailsTitle'>Genre:</p><p>{metadata?.Genre}</p>
                                                </div>
                                                <div className='starring'>
                                                    <div className='detailsDiv'><p className='detailsTitle'>Director:</p><p>{metadata?.Director}</p></div>
                                                    <div className='detailsDiv'>
                                                        <p className='detailsTitle'>Starring:</p>
                                                        <div>
                                                            {metadata?.Actors?.split(',').map((actor, i) => (
                                                                <p key={i}>{actor.trim()}</p>
                                                            ))}
                                                        </div>
                                                    </div>
                                                    <div className='detailsDiv'>
                                                        <p className='detailsTitle'>Awards:</p>
                                                        <p>{metadata?.Awards}</p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                            {selectedMovie && (
                                <VideoOverlay
                                    path={selectedMovie.path}
                                    onClose={() => setSelectedMovie(null)}
                                />
                            )}
                        </>
                    </div>
                )}
            </div>
        </>
    );
}

function VideoOverlay({ path = '', onClose }) {
    if (!path) return null;

    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/file?path=${encodeURIComponent(path)}&token=${token}`;
    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>✖</button>
                <video controls autoPlay className="video-player">
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}
