import { useAuth } from './authContext';
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Header from './Header';
import SeriesRow from './SeriesRow';
import MovieRow from './MovieRow';
import RecentlyAddedRow from './recentlyAddedRow';

const API_URL = process.env.REACT_APP_API_URL;

function Home() {
    const navigate = useNavigate();
    const location = useLocation();
    const { isLoggedIn } = useAuth();
    const [selectedMovie, setSelectedMovie] = useState(null);

    // Sync URL with selectedMovie
    React.useEffect(() => {
        const params = new URLSearchParams(location.search);
        const play = params.get('play');

        if (!play && selectedMovie) {
            setSelectedMovie(null);
        }

        if (play && !selectedMovie) {
            setSelectedMovie({ filename: play });
        }
    }, [location.search]);

    return (
        <>
            <Header />
            <div className='mainDiv'>
                <Banner />
                {isLoggedIn && (
                    <>
                        <RecentlyAddedRow
                            title="Most watched movies"
                            selectedMovie={selectedMovie}
                            setSelectedMovie={(movie) => {
                                setSelectedMovie(movie);
                                navigate(`?play=${encodeURIComponent(movie.filename)}`);
                            }}
                        />

                        <MovieRow
                            title="Most watched movies"
                            selectedMovie={selectedMovie}
                            setSelectedMovie={(movie) => {
                                setSelectedMovie(movie);
                                navigate(`?play=${encodeURIComponent(movie.filename)}`);
                            }}
                            topPicks={true}
                        />

                        <SeriesRow
                            title="Most Watched Series"
                            onSelectSeries={(seriesName) => navigate(`/series?name=${encodeURIComponent(seriesName)}`)}
                            topSeries={true}
                        />
                    </>
                )}
                {selectedMovie && (
                    <VideoOverlay
                        filename={selectedMovie.filename}
                        onClose={() => {
                            setSelectedMovie(null);
                            navigate('', { replace: true }); // Clear ?play=... from the URL
                        }}
                    />
                )}
            </div>
        </>
    );
}

function Banner() {
    const { isLoggedIn, user } = useAuth();

    return (
        <div className="banner">
            {isLoggedIn ? (
                <>
                    <div className="Align">
                        <h1 className="valkommen">
                            Welcome to Erkflix <span className="namnRubrik">{user.name}</span>!
                        </h1>
                    </div>
                </>
            ) : (
                <>
                    <div className="Align">
                        <h1 className="valkommen">Welcome to Erkflix</h1>
                    </div>
                    <h3 className="valkommen2">Please sign in to access the content!</h3>
                </>
            )}
        </div>
    );
}

// Video Overlay
function VideoOverlay({ filename, onClose }) {
    const token = localStorage.getItem('token');
    const videoUrl = `${API_URL}/api/stream/movie/${encodeURIComponent(filename)}?token=${token}`;

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>âœ–</button>
                <video
                    controls
                    autoPlay
                    className="video-player"
                >
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}

export default Home;
