// src/components/Watch.js
import React, { useEffect, useState, useRef } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import api from '../Api';

export default function Watch() {
    const [searchParams] = useSearchParams();
    const path = searchParams.get('path');
    const movieFolder = searchParams.get('movie');
    const navigate = useNavigate();

    const [metadata, setMetadata] = useState(null);
    const metadataCache = useRef({});

    const getVideoUrl = () => {
        if (path) {
            return `${api.defaults.baseURL}/api/stream/file?path=${encodeURIComponent(path)}`;
        }
        if (movieFolder && metadata?.filename) {
            return `${api.defaults.baseURL}/api/stream/movie/${encodeURIComponent(movieFolder)}/${encodeURIComponent(metadata.filename)}`;
        }
        return '';
    };

    return (
        <>
            <div className='mainDiv'>
                <div className='videoOverlay'>
                    <div className="video-wrapper">
                        <button className="close-button" onClick={() => navigate(-1)}>âœ–</button>
                        <video controls autoPlay className="video-player">
                            <source src={getVideoUrl()} type="video/mp4" />
                            Your browser does not support the video tag.
                        </video>
                    </div>
                </div>
            </div>
        </>
    );
}
