// src/components/Watch.js
import React, { useEffect, useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import api from '../Api';
import { useAuth } from '../authContext';

export default function Watch() {
    const [searchParams] = useSearchParams();
    const path = searchParams.get('path');
    const movieFolder = searchParams.get('movie');
    const navigate = useNavigate();
    const { accessToken } = useAuth();

    const [metadata, setMetadata] = useState(null);

    useEffect(() => {
        if (movieFolder) {
            const fetchMetadata = async () => {
                try {
                    const res = await api.get(`/api/movie-metadata/${encodeURIComponent(movieFolder)}`);
                    setMetadata(res.data);
                } catch (err) {
                    console.error('❌ Failed to load movie metadata:', err);
                }
            };
            fetchMetadata();
        }
    }, [movieFolder]);

    useEffect(() => {
        const saveProgress = () => {
            if (videoRef.current && user) {
                const time = videoRef.current.currentTime;
                axios.post('/api/progress', {
                    fileName: videoFileName,
                    type: contentType, // 'movie' or 'series'
                    time,
                });
            }
        };

        const interval = setInterval(saveProgress, 10000); // Every 10 seconds

        return () => {
            saveProgress(); // Save one last time on unmount
            clearInterval(interval);
        };
    }, []);

    useEffect(() => {
        const fetchProgress = async () => {
            const res = await axios.get(`/api/progress?fileName=${videoFileName}`);
            const savedTime = res.data?.time;
            if (videoRef.current && savedTime) {
                videoRef.current.currentTime = savedTime;
            }
        };
        fetchProgress();
    }, []);


    const videoUrl = (() => {
        if (path && accessToken) {
            return `${api.defaults.baseURL}/api/stream/file?path=${encodeURIComponent(path)}&token=${accessToken}`;
        }

        if (movieFolder && metadata?.filename && accessToken) {
            return `${api.defaults.baseURL}/api/stream/movie/${encodeURIComponent(movieFolder)}/${encodeURIComponent(metadata.filename)}?token=${accessToken}`;
        }

        return null;
    })();

    const subtitleUrl = (() => {
        if (path) {
            const vttPath = path.replace(/\.[^.]+$/, '.vtt'); // change .mp4 to .vtt
            return `${api.defaults.baseURL}/media/Series/${encodeURIComponent(vttPath)}`;
        }

        if (movieFolder && metadata?.filename) {
            const vttPath = `${movieFolder}/${metadata.filename}`.replace(/\.[^.]+$/, '.vtt');
            return `${api.defaults.baseURL}/media/Movies/${encodeURIComponent(vttPath)}`;
        }

        return null;
    })();

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={() => navigate(-1)}>✖</button>
                {videoUrl ? (
                    <video controls autoPlay className="video-player" crossOrigin="anonymous">
                        <source src={videoUrl} type="video/mp4" />
                        {subtitleUrl && (
                            <track
                                label="English"
                                kind="subtitles"
                                srcLang="en"
                                src={subtitleUrl}
                                default
                            />
                        )}
                        Your browser does not support the video tag.
                    </video>
                ) : (
                    <p>Loading video...</p>
                )}
            </div>
        </div>
    );
}
