// src/authContext.js
import { createContext, useContext, useEffect, useState, useRef } from 'react';
import { jwtDecode } from 'jwt-decode';
import api from './Api';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const refreshTimeout = useRef();

  // ðŸ” Refresh token when app loads
  useEffect(() => {
    refreshAccessToken();
  }, []);

  // âœ… Attach access token to all API requests
  useEffect(() => {
    const interceptor = api.interceptors.request.use(config => {
      if (accessToken) {
        config.headers.Authorization = `Bearer ${accessToken}`;
      }
      return config;
    });

    return () => api.interceptors.request.eject(interceptor);
  }, [accessToken]);

  const refreshAccessToken = async () => {
    try {
      const res = await api.post('/api/token', {}, { withCredentials: true }); // âœ… include cookies
      const newToken = res.data.accessToken;

      const decoded = jwtDecode(newToken);
      setUser(decoded);
      setAccessToken(newToken);
      setIsLoggedIn(true);

      const timeout = (decoded.exp - Date.now() / 1000) * 1000;
      clearTimeout(refreshTimeout.current);
      refreshTimeout.current = setTimeout(refreshAccessToken, timeout - 2000); // refresh 2s before expiry
    } catch (err) {
      logout(); // token invalid or expired
    }
  };

  const login = (token) => {
    const decoded = jwtDecode(token);
    setUser(decoded);
    setAccessToken(token);
    setIsLoggedIn(true);

    const timeout = (decoded.exp - Date.now() / 1000) * 1000;
    clearTimeout(refreshTimeout.current);
    refreshTimeout.current = setTimeout(refreshAccessToken, timeout - 2000);
  };

  const logout = async () => {
    try {
      await api.post('/api/logout');
    } catch (err) {
      console.error('Logout error:', err);
    }

    setUser(null);
    setAccessToken(null);
    setIsLoggedIn(false);
    clearTimeout(refreshTimeout.current);
  };

  return (
    <AuthContext.Provider value={{ user, accessToken, isLoggedIn, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
