import { useAuth } from './authContext';
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Header from './Header';
import SeriesRow from './SeriesRow';
import MovieRow from './MovieRow';
import RecentlyAddedRow from './recentlyAddedRow';

const API_URL = process.env.REACT_APP_API_URL;

function Home() {
    const navigate = useNavigate();
    const { isLoggedIn } = useAuth();
    const [selectedMovie, setSelectedMovie] = useState(null);

    return (
        <>
            <Header />
            <div className='mainDiv'>
                <Banner />
                <div className="movie-row" id='movie-row-Home'>
                    {isLoggedIn && (
                        <>
                            <RecentlyAddedRow
                                title="Recently added"
                                selectedMovie={selectedMovie}
                                setSelectedMovie={(movie) => {
                                    setSelectedMovie({ filename: movie.filename, folder: movie.folder });
                                }}
                            />
                            <MovieRow
                                title="Most watched movies"
                                selectedMovie={selectedMovie}
                                setSelectedMovie={(movie) => {
                                    setSelectedMovie({ filename: movie.filename, folder: movie.folder });                                   
                                }}
                                topPicks={true}
                            />
                            <SeriesRow
                                title="Most Watched Series"
                                onSelectSeries={(seriesName) => navigate(`/series?name=${encodeURIComponent(seriesName)}`)}
                                topSeries={true}
                            />
                        </>
                    )}
                </div>
                {selectedMovie && (
                    <VideoOverlay
                        filename={selectedMovie.filename}
                        folder={selectedMovie.folder}
                        onClose={() => {
                            setSelectedMovie(null);
                        }}
                    />

                )}
            </div>
        </>
    );
}

function Banner() {
    const { isLoggedIn, user } = useAuth();

    return (
        <div className="banner">
            {isLoggedIn ? (
                <>
                    <div className="Align">
                        <h1 className="valkommen">
                            Welcome to Erkflix <span className="namnRubrik">{user.name}</span>!
                        </h1>
                    </div>
                </>
            ) : (
                <>
                    <div className="Align">
                        <h1 className="valkommen">Welcome to Erkflix</h1>
                    </div>
                    <h3 className="valkommen2">Please sign in to access the content!</h3>
                </>
            )}
        </div>
    );
}

// Video Overlay
function VideoOverlay({ folder, filename, onClose }) {
    const token = localStorage.getItem('token');

    if (!folder || !filename) {
        return null; // Don't render if something is missing
    }

    const videoUrl = `${API_URL}/api/stream/movie/${encodeURIComponent(folder)}/${encodeURIComponent(filename)}?token=${token}`;
    console.log('Streaming URL:', videoUrl);

    return (
        <div className="video-overlay">
            <div className="video-wrapper">
                <button className="close-button" onClick={onClose}>âœ–</button>
                <video
                    controls
                    autoPlay
                    className="video-player"
                >
                    <source src={videoUrl} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}


export default Home;
