// Components/ProgressBar.js
import React from 'react';

export default function ProgressBar({ time, duration }) {
  if (!time || !duration || time < 10) return null;

  const percent = Math.min((time / duration) * 100, 100);
  if (percent >= 98) return null;

  return (
    <div className="absolute bottom-0 left-0 w-full h-[4px] bg-white/30">
      <div
        className="h-full bg-red-600"
        style={{ width: `${percent}%` }}
      />
    </div>
  );
}
