import { useEffect, useState } from 'react';
import { Link, NavLink } from 'react-router-dom';
import { useAuth } from '../authContext';

function Header() {
  const { isLoggedIn } = useAuth();
  const [navSlideIn, setNavSlideIn] = useState(false);
  const [ready, setReady] = useState(false); // NEW: hide nav until checked

  useEffect(() => {
    const justLoggedIn = sessionStorage.getItem('justLoggedIn') === 'true';
    if (justLoggedIn) {
      setNavSlideIn(true);
      sessionStorage.removeItem('justLoggedIn');
    }
    setReady(true); // We’re ready to render now
  }, []);

  return (
    <header className={`header`}>
      <Link className='logga' to="/Home">
        Erkflix
      </Link>
      {isLoggedIn && ready && (
        <nav className={navSlideIn ? 'nav-slide-in' : ''}>
          <NavLink to="/Home" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`}>Home</NavLink>
          <NavLink to="/Wish" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`}>Wishlist</NavLink>
          <NavLink to="/Movies" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`}>Movies</NavLink>
          <NavLink to="/Series" className={({ isActive }) => `nav ${isActive ? 'active' : ''}`}>Series</NavLink>
        </nav>
      )}
    </header>
  );
}

export default Header;
