import { useAuth } from '../authContext';
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Header from '../Components/Header';
import SeriesRow from '../Components/SeriesRow';
import MovieRow from '../Components/MovieRow';
import RecentlyAddedRow from '../Components/recentlyAddedRow';
import api from '../Api';

function Home() {
    const navigate = useNavigate();
    const location = useLocation();
    const { isLoggedIn } = useAuth();
    const [selectedMovie, setSelectedMovie] = useState(null);

    React.useEffect(() => {
        const params = new URLSearchParams(location.search);
        const play = params.get('play');
        const folder = params.get('folder');

        if (!play && selectedMovie) {
            setSelectedMovie(null);
        }

        if (play && folder && !selectedMovie) {
            setSelectedMovie({ filename: play, folder });
        }
    }, [location.search, selectedMovie]);

    return (
        <>
            <Header />
            <div className='mainDiv'>
                <Banner />
                <div className="movie-row" id='movie-row-Home'>
                    {isLoggedIn && (
                        <>
                            <RecentlyAddedRow
                                title="Recently added"
                                selectedMovie={selectedMovie}
                                setSelectedMovie={(movie) => {
                                    setSelectedMovie({ filename: movie.filename, folder: movie.folder });
                                    navigate(`?play=${encodeURIComponent(movie.filename)}&folder=${encodeURIComponent(movie.folder)}`);

                                }}
                            />
                            <MovieRow
                                title="Most watched movies"
                                selectedMovie={selectedMovie}
                                setSelectedMovie={(movie) => {
                                    setSelectedMovie({ filename: movie.filename, folder: movie.folder });
                                    navigate(`?play=${encodeURIComponent(movie.filename)}&folder=${encodeURIComponent(movie.folder)}`);
                                }}
                                topPicks={true}
                            />
                            <SeriesRow
                                title="Most Watched Series"
                                onSelectSeries={(seriesName) => navigate(`/series?name=${encodeURIComponent(seriesName)}`)}
                                topSeries={true}
                            />
                        </>
                    )}
                </div>
                {selectedMovie && (
                    <VideoOverlay
                        filename={selectedMovie.filename}
                        folder={selectedMovie.folder}
                        onClose={() => {
                            setSelectedMovie(null);
                            navigate(-1);
                        }}
                    />
                )}
            </div>
        </>
    );
}

function Banner() {
    const { isLoggedIn, user } = useAuth();

    return (
        <div className="banner">
            {isLoggedIn ? (
                <>
                    <div className="Align">
                        <h1 className="valkommen">
                            Welcome to Erkflix <span className="namnRubrik">{user.name}</span>!
                        </h1>
                    </div>
                </>
            ) : (
                <>
                    <div className="Align">
                        <h1 className="valkommen">Welcome to Erkflix</h1>
                    </div>
                    <h3 className="valkommen2">Please sign in to access the content!</h3>
                </>
            )}
        </div>
    );
}


export default Home;
